import React, { Component } from 'react';
import ContactList from './ContactList';
import EducationInfo from './EducationInfo';
import { connect } from 'react-redux'
import {editName, editRegion, editDate, editEducation} from '../../store/actions';
import {contact, education} from '../util';
import { Button, Form, Input, Select, DatePicker } from 'element-react';
import 'element-theme-default';

  const InfoForm = ({dispatch}) => {
    const [name, setName] = useState('');
    // onSubmit(e) {
    //     console.log(this.state.user);
    //     e.preventDefault();
    // }

    // onChange(key, value) {
    //   this.state.user[key] = value;
    //   this.forceUpdate();
    // }

    // render() {
      return (
        <Form labelWidth="80">
          <Form.Item label="姓名 ">
            <Input value={name} onChange={()=> {dispatch(editName(this.value)); setName(this.Value)}}></Input>
          </Form.Item>
          <Form.Item label="所在地 " onChange={()=> dispatch(editRegion(this.value))}>
            <Select placeholder="请选择活动区域" >
              <Select.Option label="区域一" value="shanghai"></Select.Option>
              <Select.Option label="区域二" value="beijing"></Select.Option>
            </Select>
          </Form.Item>
          <Form.Item label="出生年月 ">
              <Form.Item prop="date1" labelWidth="0px">
                <DatePicker
                  // value={this.state.user.date}
                  placeholder="选择日期"
                  onChange={()=> dispatch(editDate(this.value))}
                />
              </Form.Item>
          </Form.Item>
          <Form.Item label="学历 ">
            <Select placeholder="请选择您的最高学历" onChange={()=> dispatch(editEducation(this.value))}>
              <Select.Option label="初中" value="初中"></Select.Option>
              <Select.Option label="高中" value="高中"></Select.Option>
              <Select.Option label="本科" value="本科"></Select.Option>
              <Select.Option label="研究生" value="研究生"></Select.Option>
              <Select.Option label="硕士" value="硕士"></Select.Option>
              <Select.Option label="博士及以上" value="博士及以上"></Select.Option>
            </Select>
          </Form.Item>
          <Form.Item label="联系方式 ">
            <ContactList></ContactList>
          </Form.Item>
          <EducationInfo></EducationInfo>
          <Form.Item>
            <Button type="primary" nativeType="submit">立即创建</Button>
            <Button>取消</Button>
          </Form.Item>
        </Form>
      )
    // }
  }

  export default connect()(InfoForm);
