import React, { useState } from 'react';
import { Button, Input, Select, Layout, Form, DatePicker} from 'element-react';
import 'element-theme-default';
import { connect } from 'react-redux';
import {editWork} from '../../store/actions';
import {workExperience} from '../util';

const WorkExperience = ({works, dispatch}) => {
    const [isOpen, setIsOpen] = useState(true);
    const [count, setCount] = useState(1);
    const [workLst, setWorkLst] = useState(works);
    const toShow = workLst.map((work)=>{return <WorkUnit key={work.index} index={work.index} work={work} remove={remove} update={update}></WorkUnit>});

    function switchState() {
        setIsOpen(!isOpen);
    }

    function add() {
        const newWork= new workExperience(count);
        setCount(count+1);
        var nlst = workLst.slice();
        nlst.push(newWork);
        setWorkLst(nlst)
        dispatch(editWork(nlst));
    }

    function update(index, obj) {
        var e;
        var nlst = workLst.slice();
        for (e of nlst) {
          if (e.index === index) {
            e = obj;
          }
        }
        setWorkLst(nlst);
        dispatch(editWork(nlst));
    }

    function remove(index){
        var nlst = workLst.slice();
        for (var i of nlst) {
          if (i.index === index) {
            nlst.splice(i, 1);
          }
        }
        setWorkLst(nlst);
        dispatch(editWork(nlst));
    }

    if (!isOpen) {
        return (
            <Form.Item label="从业经历 ">
              <Button type="primary small" onClick={switchState}>Expand</Button>
            </Form.Item>
        )
    }
    return(
        <Form.Item label="从业经历 ">
            {toShow}
            <Button type="primary small" onClick={add}>Add</Button>
            <Button type="primary small" onClick={switchState}>Collapse</Button>
        </Form.Item>
    )
}

function WorkUnit(props){
    var index = props.index;
    var work = props.work;
    const update = props.update;
    const remove = props.remove;

    function onChange(value, key) {
        work[key] = value;
        update(work.index, work);
    }

    return(
        <div>
        <Form.Item label="所在单位： ">
          <Input placeholder="请输入所在单位名称" value={work.company} onChange={(e)=>onChange(e, 'company')}></Input>
        </Form.Item>
        <Form.Item label="职位： ">
          <Input placeholder="请输入职位名称" value={work.jobTitle} onChange={(e)=>onChange(e, 'jobTitle')}></Input>
        </Form.Item>
        <Form.Item label="时间： ">
          <Layout.Col span="5">
            <Form.Item labelWidth="0px">
              <DatePicker
                value={work.startDate}
                placeholder="选择开始日期"
                onChange={(e)=> onChange.bind(e, 'startDate')}
              />
            </Form.Item>
          </Layout.Col>
          <Layout.Col className="line" span="1">-</Layout.Col>
          <Layout.Col span="5">
            <Form.Item labelWidth="0px">
              <DatePicker
                value={work.endDate}
                placeholder="选择结束日期"
                onChange={(e)=>onChange.bind(e, 'endDate')}
              />
            </Form.Item>
            </Layout.Col>
          </Form.Item>
        <Form.Item label="工作内容： ">
          <Input placeholder="请输入主要工作职责描述"  value={work.description} onChange={(e)=> onChange(e, "desxription")}></Input>
        </Form.Item>
        <Button type="text" icon="delete" onClick={() => {remove(index)}}></Button>
      </div>
    );
}

function mapStateToProps(state) {
    var works = state.updateUser.user.workExperience;
    // console.log(eduExp);
    return {works};
}

export default connect()(WorkExperience);
