import { EDIT_NAME, EDIT_DATE, EDIT_REGION, EDIT_EDUCATION } from "./actions";
import { combineReducers } from "redux";


const initialState = {
    user: {
        name: '',
        date: '',
        region:'',
        education:'',
        contact:[],
        educationExperience:[]
    },
}

function updateUser(state=initialState, action) {
    var user = state.user;
    switch(action.type) {
        case EDIT_NAME:
            user.name = action.text;
            console.log(state);
            return Object.assign({}, state, user);
        case EDIT_DATE:
            user.date = action.text;
            return Object.assign({}, state, {date: action.text});
        case EDIT_REGION:
            user.region = action.text;
            return Object.assign({}, state, {region: action.text});
        default:
            return state;
    }
}

function updateStyle(state=initialState, action) {
    return state;
}

export const updateApp = combineReducers({
    updateUser,
    updateStyle
})
