import React, { Component, useState} from 'react';
import {education} from '../util';
import { Button, Input, Select, Layout, Form} from 'element-react';
import 'element-theme-default';
import { editContact } from '../../store/actions';

const EducationInfo = ({dispatch}) => {
      var edu1 = new education(0);
      const [isOpen, setIsOpen] = useState(true);
      const [count, setCount] = useState(0);
      const [eduLst, setEduLst] = useState([edu1]);
      const toShow = eduLst.map((edu)=> <EducationUnit key={editContact.index} value={edu} callBk={update}></EducationUnit>);

      function switchState() {
        setIsOpen(!isOpen);
      }

      function add() {
        setCount(count+1);
        const newEdu= new education(count);
        var nlst = eduLst.slice();
        nlst.push(newEdu);
        setEduLst(nlst)
      }

      function update() {
        console.log(update);
      }

      if(!isOpen) {
          return(
            <Form.Item label="学术经历 ">
              <Button type="primary small" onClick={switchState}>Expand</Button>
            </Form.Item>
          )
      } else {
          return(
              <Form.Item label="学术经历 ">
                  {toShow}
                  <Button type="primary small" onClick={add}>Add</Button>
                  <Button type="primary small" onClick={switchState}>Collapse</Button>
              </Form.Item>
          )
      }
}

function EducationUnit (props) {
  let edu = props.value;
  let index = props.index;

  function onChange(key, value) {
    edu[key] = value;
  };

  return(
    <div>
      <Form.Item label="所在学校： ">
        <Input placeholder="请输入就读学校名称"></Input>
      </Form.Item>
      <Form.Item label="时间： ">
        <Layout.Col span="11">
          <Form.Item prop="开始时间" labelWidth="0px">
            <DatePicker
              value={this.edu.startTime}
              placeholder="选择日期"
              onChange={this.onChange.bind(this, 'date1')}
            />
          </Form.Item>
        </Layout.Col>
        <Layout.Col className="line" span="2">-</Layout.Col>
        <Layout.Col span="11">
          <Form.Item prop="date2" labelWidth="0px">
            <TimePicker
              value={this.state.form.date2}
              selectableRange="18:30:00 - 20:30:00"
              placeholder="选择时间"
              onChange={this.onChange.bind(this, 'date2')}
            />
          </Form.Item>
          </Layout.Col>
        </Form.Item>
      <Form.Item label="所在学校： ">
        <Input placeholder="请输入就读学校名称"></Input>
      </Form.Item>
    </div>

  );
}

export default EducationInfo;
